    private void toggleOnOffActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_toggleOnOffActionPerformed
		if (toggleOnOff.isSelected()) {
			// Gisable GUI edits
			spinPort.setEnabled(false);
			
			// Set & start server
			Server.setCentralServerAddress(tfCentralServerAddress.getText());
			server.setPort((int)spinPort.getValue()); // TODO catch error if port closed
			server.signalStart();
		}
		else {
			// Enable GUI edits
			spinPort.setEnabled(true);
			
			// Stop server
			server.stopServer();
		}
    }//GEN-LAST:event_toggleOnOffActionPerformed

