	private void initServerBackend() {
		server = new Server();
		server.start();
	}

	private void updateGUI() {
		tfAddress.setText(server.getAddress());
		spinPort.setValue(server.getPort());
		spinCentralServerPort.setValue(CentralServer.PORT);
	}
